/**
 * 插件: 将货币数字转化为大写
 * 用法: upCalc(123.12)
 * value: 千万位内,精确到两位小数
 *
 * author: yeliex
 * website: yeliex.com
 * version: 1.1
 * lastUpdate: 2015年09月03日21:44:36
 *
 * based: jQuery
 *
 * ex: upCalc(123);
 * return: 一百贰拾叁
 */
function upCalc(j){if(j===0){return"零"}var b="壹贰叁肆伍陆柒捌玖拾";var a="元拾佰仟万拾佰仟";var h="分角";var c="";var g=String(j).split(".");g[2]=g[0].length;var f,e,k=g[0];for(var d=1;d<=g[2];d++){e=f;f=Math.floor(k/(Math.pow(10,g[2]-d)));k=k-f*(Math.pow(10,g[2]-d));if(f==0){if(d==g[2]){c+="元"}else{if(d<=g[2]-4&&e!=0&&g[2]>=6){c+="万"}else{}}}else{if(f!=0&&e==0&&d==g[2]){c=c+"零"+b.substr(f-1,1)+"元"}else{c=c+b.substr(f-1,1)+a.substr(g[2]-d,1)}}}f=1;for(d=0;d<2;d++){e=f;f=g[1].substr(d,1);if(f===0&&e===0){c+="整"}else{if(f!==0){c+=b.substr(f-1,1)+h.substr(d-1,1)}else{}}}c=c.substr(1);return c};
